str = ''.downcase

l = str.length
i = 0
longest = ''

while i < l
  char = str[i]
#  puts char
#  exit
  ii = i + 1
  while n = str.index(char, ii)
    substr = str[i..n]
    if substr == substr.reverse
      longest = substr if longest.length < substr.length
    end

    ii = n+1
  end
  i += 1
end

puts longest
